mapper.select:
select sum(num) as sumNum from  t_duck_breeding  where  user_id = #{user_id,jdbcType=BIGINT}
and add_time >= #{startTime,jdbcType=TIMESTAMP}
and #{endTime,jdbcType=TIMESTAMP} >= add_time

SELECT SUM(produce DIV 5) produce FROM t_duck_breeding
WHERE user_id = #{userId,jdbcType=BIGINT}
 AND STATUS IN(1,2) AND produce > 0

SELECT IFNULL(SUM(num_freeze),0) FROM t_duck_breeding
WHERE user_id = #{userId,jdbcType=BIGINT}
and STATUS IN(1,2)
