mapper.update:
UPDATE t_egg_warehouse
SET egg = egg - #{num,jdbcType=INTEGER},
egg_doing = egg_doing + #{num,jdbcType=INTEGER},
update_time = now()
where user_id = #{userId,jdbcType=BIGINT}
and egg >=#{num,jdbcType=INTEGER}

UPDATE t_egg_warehouse
SET duck = duck - #{num,jdbcType=INTEGER},
update_time = now()
where user_id = #{userId,jdbcType=BIGINT}
and duck >=#{num,jdbcType=INTEGER}

UPDATE t_egg_warehouse set
update_time = NOW(),
duck = duck + #{stealNum,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=BIGINT}

UPDATE t_egg_warehouse
SET  duck= duck - #{num}
where user_id = #{userId}
and (duck - #{num,jdbcType=DECIMAL}) >0
