mapper.update:
UPDATE t_user
SET grade =#{grade,jdbcType=BIGINT}
WHERE id = #{userId,jdbcType=BIGINT}

UPDATE t_user
SET is_auth =#{auth,jdbcType=BIGINT}
WHERE id = #{userId,jdbcType=BIGINT}

UPDATE t_user
SET password =#{pwd}
WHERE id = #{userId,jdbcType=BIGINT}

 UPDATE t_user
 SET name =#{name},
img_url= #{imgUrl}
 WHERE id = #{userId,jdbcType=BIGINT}

mapper.select:
SELECT

FROM `t_user`
WHERE parent_id = #{userId,jdbcType=BIGINT}
ORDER BY add_time DESC
LIMIT #{offset}, #{size}

SELECT

FROM `t_user`
WHERE parent_id IN(
SELECT
id
FROM `t_user` WHERE
parent_id = #{userId,jdbcType=BIGINT}
)
ORDER BY add_time DESC
LIMIT #{offset}, #{size}
